import 'intl';
import 'intl/locale-data/jsonp/en';

import * as React from 'react';

import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer } from '@react-navigation/native';

import { Provider as PaperProvider, LightTheme, Modal, withTheme } from 'react-native-paper';

import * as Notifications from 'expo-notifications';

import AlarmNotify from './components/AlarmNotify';
import HomeScreen from './screens/HomeScreen';
import CreationScreen from './screens/CreationScreen';
import RadioPlayer from './Lib/RadioPlayer';

const Stack = createNativeStackNavigator();

Notifications.setNotificationHandler({
    handleNotification: async () => ({
        shouldShowAlert: true,
        shouldPlaySound: true,
        shouldSetBadge: true,
    }),
});

export default class App extends React.Component {

    constructor() {
        super();

        Notifications.addNotificationReceivedListener(notification => {
            RadioPlayer.instance.play({
                uri: notification.request.content.data.radioUri
            });

            this.state.isAlarmNotifyVisible = true;
            this.state.currentRadioName = notification.request.content.data.radioName;
            this.setState(this.state);
        });
    }

    state = {
        isAlarmNotifyVisible: false,
        currentRadioName: "",
    }

    _hideAlarmNotify() {
        RadioPlayer.instance.stop();
        this.state.isAlarmNotifyVisible = false;
        this.setState(this.state);
    }

    render() {
        return (
            <PaperProvider theme={LightTheme}>
                <NavigationContainer>
                    <Stack.Navigator initialRouteName="Home" screenOptions={{ header: (props) => { } }}>
                        <Stack.Screen name="Home" component={withTheme(HomeScreen)} />
                        <Stack.Screen name="Creation" component={withTheme(CreationScreen)} />
                    </Stack.Navigator>
                </NavigationContainer>
                <Modal visible={this.state.isAlarmNotifyVisible} dismissable={false} >
                    <AlarmNotify theme={LightTheme} radioName={this.state.currentRadioName} onHide={this._hideAlarmNotify.bind(this)} />
                </Modal>
            </PaperProvider>
        );
    }

}
