import * as Notifications from 'expo-notifications';

import AppDefines from '../defines/AppDefines';

class Alarm {
    constructor(hours, minutes, radioName, radioUri)
    {
        this.hours = hours;
        this.minutes = minutes;
        this.radioName = radioName;
        this.radioUri = radioUri;
        this.notifyId = '';
        this.IsActive = false;
    }

    static ConstructFromDateAndRadio({ date, radio }) {
        return new Alarm(date.getHours(), date.getMinutes(), radio.title, radio.uri);
    }

    static ConstructFromJson({ jsonObject }) {
        const alarm = new Alarm(jsonObject.hours, jsonObject.minutes, jsonObject.radioName, jsonObject.radioUri);
        alarm.notifyId = jsonObject.notifyId;
        alarm.IsActive = jsonObject.IsActive;
        return alarm;
    }

    Date() {
        let result = new Date();
        result.setHours(this.hours);
        result.setMinutes(this.minutes);
        result.setSeconds(0);

        if (result < new Date()) {
            result = new Date(result.getTime() + 86400000);
        }

        return result;
    }

    async activate() {
        this.IsActive = true;
        this.notifyId = await Notifications.scheduleNotificationAsync({
            content: {
                title: AppDefines.text.titleNotification,
                data: {
                    radioUri: this.radioUri,
                    radioName: this.radioName,
                },
            },
            trigger: {
                hour: this.hours,
                minute: this.minutes,
                repeats: true,
            },
        });
    }

    async deactivate() {
        this.IsActive = false;
        try {
            await Notifications.cancelScheduledNotificationAsync(this.notifyId);
        } catch (e) {
            console.log("[Alarm] Could not cancel notification with " + e)
        }
        this.notifyId = '';
    }
}

export default Alarm;
